$(function () {
  "use strict";
  $(".app-content");
  var o = $(".app-cart-overlay"),
    s = $(".sidebar-right"),
    a = ($(".cart-manager-application"), $(".app-cart-sidebar-info")),
    e = $(".app-cart-content"),
    i = $(".app-cart-sidebar-right");
  if (0 < e.length) new PerfectScrollbar(".app-cart-content");
  if (0 < i.length) new PerfectScrollbar(".app-cart-sidebar-content");
  if (0 < a.length) new PerfectScrollbar(".app-cart-sidebar-info");
  $(".menu-toggle, .close-icon, .app-cart-overlay").on("click", function (e) {
    s.removeClass("show"), o.removeClass("show"), a.removeClass("show")
  }), $(".app-cart-info").on("click", function () {
    a.addClass("show"), o.addClass("show")
  }), $(".app-cart-sidebar-close").on("click", function () {
    s.removeClass("show"), o.removeClass("show")
  }), $(".sidebar-toggle").on("click", function (e) {
    e.stopPropagation(), s.addClass("show"), o.addClass("show")
  });
  var l = $(".app-cart-sidebar-content .list-group-messages a,.list-group-labels a");
  $(l).on("click", function () {
    var e = $(this);
    l.removeClass("active"), e.addClass("active"), e.find(".livicon-evo").updateLiviconEvo({
      strokeColor: "#5A8DEE"
    }), l.not(".active").find(".livicon-evo").updateLiviconEvo({
      strokeColor: "#475f7b"
    })
  }), $(window).on("resize", function () {
    768 < $(window).width() && o.hasClass("show") && (s.removeClass("show"), o.removeClass("show"), a.removeClass("show"))
  })
});






// Date object
  var today = new Date();

// Current Date
  var date = today.getDate().toString().padStart(2, "0")+'/'+(today.getMonth() + 1).toString().padStart(2, "0")+'/'+today.getFullYear();
  document.getElementById("current_date").innerHTML = date;

// Current Time
  var time = today.getHours() + ":" + today.getMinutes() /*+ ":" + today.getSeconds()*/;
  document.getElementById("current_time").innerHTML = time;